import React, { createContext, useContext, useState, useEffect } from 'react';

const CategoryContext = createContext();

export function useCategories() {
  return useContext(CategoryContext);
}

export function CategoryProvider({ children }) {
  const [categories, setCategories] = useState([
    { id: 'default', name: 'ãƒ‡ãƒ•ã‚©ãƒ«ãƒˆ', color: '#1976d2' },
    { id: 'work', name: 'ä»•äº‹', color: '#f44336' },
    { id: 'personal', name: 'ãƒ—ãƒ©ã‚¤ãƒ™ãƒ¼ãƒˆ', color: '#4caf50' },
    { id: 'shopping', name: 'è²·ã„ç‰©', color: '#ff9800' },
  ]);

  // ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã‹ã‚‰ã‚«ãƒ†ã‚´ãƒªã‚’èª­ã¿è¾¼ã‚€
  useEffect(() => {
    const storedCategories = localStorage.getItem('categories');
    if (storedCategories) {
      try {
        setCategories(JSON.parse(storedCategories));
      } catch (e) {
        console.error('ã‚«ãƒ†ã‚´ãƒªã®èª­ã¿è¾¼ã¿ã«å¤±æ•—ã—ã¾ã—ãŸ', e);
      }
    }
  }, []);

  // ã‚«ãƒ†ã‚´ãƒªãŒå¤‰æ›´ã•ã‚ŒãŸã‚‰ãƒ­ãƒ¼ã‚«ãƒ«ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã«ä¿å­˜
  useEffect(() => {
    localStorage.setItem('categories', JSON.stringify(categories));
  }, [categories]);

  // ã‚«ãƒ†ã‚´ãƒªã‚’è¿½åŠ 
  const addCategory = (category) => {
    setCategories([...categories, { ...category, id: Date.now().toString() }]);
  };

  // ã‚«ãƒ†ã‚´ãƒªã‚’æ›´æ–°
  const updateCategory = (id, updatedCategory) => {
    setCategories(
      categories.map((cat) => (cat.id === id ? { ...cat, ...updatedCategory } : cat))
    );
  };

  // ã‚«ãƒ†ã‚´ãƒªã‚’å‰Šé™¤
  const deleteCategory = (id) => {
    setCategories(categories.filter((cat) => cat.id !== id));
  };

  const value = {
    categories,
    addCategory,
    updateCategory,
    deleteCategory,
  };

  return <CategoryContext.Provider value={value}>{children}</CategoryContext.Provider>;
}

