          <Button
            fullWidth
            variant="contained"
            color="primary"
            type="submit"
            startIcon={<AddIcon />}
            disabled={!title.trim()}
          >
            追加
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
}

export default AddTodo;

