        return todoList.filter(todo => {
          if (!todo.due) return false;
          const dueDate = new Date(todo.due);
          dueDate.setHours(0, 0, 0, 0);
          return dueDate.getTime() < today.getTime();
        });
      default:
        return todoList;
    }
  };

