      default:
        return (
          <>
            <AddTodo onAddTodo={handleAddTodo} />
            <TodoList
              todos={getFilteredTodos()}
              onToggle={handleToggleTodo}
              onDelete={handleDeleteTodo}
            />
          </>
        );
    }
  };

  // 現在のビューに基づいてタイトルを取得
  const getViewTitle = () => {
    if (currentView === 'categories') {
      return 'カテゴリ管理';
    } else if (currentView === 'category' && selectedCategoryId) {
      // カテゴリ名を取得するロジックが必要
      return 'カテゴリ別タスク';
    } else {
      switch (selectedDateFilter) {
        case 'today': return '今日';
        case 'tomorrow': return '明日';
        case 'after_tomorrow': return '明後日';
        case 'past': return '過去のタスク';
        default: return 'すべてのタスク';
      }
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AuthProvider value={{ isAuthenticated, setIsAuthenticated }}>
        <CategoryProvider>
          <Box sx={{ display: 'flex', height: '100vh', overflow: 'hidden' }}>
            {isAuthenticated && (
              <Sidebar
                onNavigate={handleNavigate}
                selectedCategory={selectedCategoryId}
                selectedDateFilter={selectedDateFilter}
                todos={todos}
              />
            )}

            <Box sx={{ 
              flexGrow: 1, 
              display: 'flex', 
              flexDirection: 'column',
              overflow: 'hidden',
              bgcolor: 'background.default'
            }}>
              <Header title={getViewTitle()} />

              <Box sx={{ 
                p: 2, 
                flexGrow: 1,
                overflow: 'auto',
              }}>
                <Paper 
                  elevation={0} 
                  sx={{ 
                    p: 2,
                    height: '100%',
                    borderRadius: 2,
                    display: 'flex',
                    flexDirection: 'column'
                  }}
                >
                  {renderContent()}
                </Paper>
              </Box>
            </Box>
          </Box>
        </CategoryProvider>
      </AuthProvider>
    </ThemeProvider>
  );
}

export default App;

