#### ステップ6: カテゴリ管理画面の作成

```javascript
// src/components/categories/CategoryManagement.js
import React, { useState } from 'react';
import {
  Box,
  Typography,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  Button,
  Divider,
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { useCategories } from '../../contexts/CategoryContext';
import { ChromePicker } from 'react-color';

function CategoryManagement() {
  const { categories, addCategory, updateCategory, deleteCategory } = useCategories();
  const [open, setOpen] = useState(false);
  const [editingCategory, setEditingCategory] = useState(null);
  const [categoryName, setCategoryName] = useState('');
  const [categoryColor, setCategoryColor] = useState('#1976d2');

  const handleOpen = (category = null) => {
    if (category) {
      setEditingCategory(category);
      setCategoryName(category.name);
      setCategoryColor(category.color);
    } else {
      setEditingCategory(null);
      setCategoryName('');
      setCategoryColor('#1976d2');
    }
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSave = () => {
    if (!categoryName.trim()) return;

    if (editingCategory) {
      updateCategory(editingCategory.id, {
        name: categoryName.trim(),
        color: categoryColor,
      });
    } else {
      addCategory({
        name: categoryName.trim(),
        color: categoryColor,
      });
    }

    handleClose();
  };

  const handleDelete = (id) => {
    if (window.confirm('このカテゴリを削除してもよろしいですか？')) {
      deleteCategory(id);
    }
  };

  return (
    <Box sx={{ mt: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
        <Typography variant="h6">カテゴリ管理</Typography>
        <Button
          variant="contained"
          color="primary"
          startIcon={<AddIcon />}
          onClick={() => handleOpen()}
        >
          新規カテゴリ
        </Button>
      </Box>

      <List>
        {categories.map((category) => (
          <React.Fragment key={category.id}>
            <ListItem
              sx={{
                borderLeft: `4px solid ${category.color}`,
                bgcolor: 'background.paper',
                mb: 1,
                borderRadius: 1,
              }}
            >
              <ListItemText primary={category.name} />
              <IconButton edge="end" onClick={() => handleOpen(category)}>
                <EditIcon />
              </IconButton>
              <IconButton edge="end" onClick={() => handleDelete(category.id)}>
                <DeleteIcon />
              </IconButton>
            </ListItem>
            <Divider />
          </React.Fragment>
        ))}
      </List>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{editingCategory ? 'カテゴリを編集' : '新規カテゴリ'}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="カテゴリ名"
            fullWidth
            value={categoryName}
            onChange={(e) => setCategoryName(e.target.value)}
          />
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              カラー
            </Typography>
            <ChromePicker
              color={categoryColor}
              onChange={(color) => setCategoryColor(color.hex)}
              disableAlpha
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>キャンセル</Button>
          <Button onClick={handleSave} color="primary">
            保存
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default CategoryManagement;
```

